class Solution:

    dirs = [(0, 1), (0, -1), (-1, 0), (1, 0)]

    def largestIsland(self, grid) -> int:
        self._r = len(grid)
        if self._r == 0:
            return 0
        self._c = len(grid[0])
        if self._c == 0:
            return 0

        self._grid = grid


        self._cccount = 0
        self._visited = [[False for _ in range(self._c)] for _ in range(self._r)]
        self._comps = [[-1 for _ in range(self._c)] for _ in range(self._r)]
        compCount = dict()

        for i in range(self._r):
            for j in range(self._c):
                if not self._visited[i][j] and grid[i][j] == 1:
                    compCount[self._cccount] = self._dfs(i, j)
                    self._cccount += 1

        count = 0
        for i in range(self._r):
            for j in range(self._c):
                if grid[i][j] == 0:
                    ijmax = 1
                    connectedComp = set()
                    for d in Solution.dirs:
                        nx, ny = i + d[0], j + d[1]
                        if self._inArea(nx, ny) and grid[nx][ny] == 1 and self._comps[nx][ny] not in connectedComp:
                            ijmax += compCount[self._comps[nx][ny]]
                            connectedComp.add(self._comps[nx][ny])
                    count = max(count, ijmax)

        return count or self._r * self._c
    

    def _dfs(self, x, y):
        self._visited[x][y] = True
        self._comps[x][y] = self._cccount
        count = 1
        for d in Solution.dirs:
            nx, ny = x + d[0], y + d[1]
            if self._inArea(nx, ny) and not self._visited[nx][ny] and self._grid[nx][ny] == 1:
                count += self._dfs(nx, ny)
        return count

    def _inArea(self, x, y):
        return x >= 0 and x < self._r and y >= 0 and y < self._c


import sys
sys.setrecursionlimit(50010)
s = Solution()

print(s.largestIsland([[1,1],[1,0]]))
